div.script:
document.addEventListener('DOMContentLoaded', () => {
// Get all tab links
const tabLinks = document.querySelectorAll('div#nav-tab a');

// Get all tab panes
const tabPanes = document.querySelectorAll('div#nav-tabContent div.tab-pane');

// Function to deactivate all tabs and hide all content
function deactivateAll() {
tabLinks.forEach(link => {
link.classList.remove('active');
});
tabPanes.forEach(pane => {
pane.classList.remove('show', 'active');
});
}

// Function to activate a tab and its content
function activateTab(tabLink) {
// Get the target pane
const targetPaneId = tabLink.getAttribute('href');
const targetPane = document.querySelector(targetPaneId);

// Activate the tab link
tabLink.classList.add('active');

// Show the target pane
targetPane.classList.add('show', 'active');
}

// Function to set default active tab
function setDefaultTab() {
// Get the default tab link (Giới thiệu)
const defaultTabLink = document.getElementById('nav-info-tab');

// Activate the default tab
activateTab(defaultTabLink);
}

// Attach click event to each tab link
tabLinks.forEach(link => {
link.addEventListener('click', (e) => {
e.preventDefault(); // Prevent the default anchor behavior
deactivateAll(); // Deactivate all tabs and hide content
activateTab(link); // Activate the clicked tab and its content
});
});

// Set default active tab on page load
setDefaultTab();
});
